INSERT INTO services (name,description,cost,duration) values ('Limpieza dental','Previene la aparición de una enfermedad o el surgimiento de una infección realizandote el tratamiento de profilaxis.',4000,'2 meses');
INSERT INTO services (name,description,cost,duration) values ('Blanqueamiento dental','Realiza el blanqueamiento de la mano de expertos que puedan instruirte en las ventajas y desventajas de cada tratamiento.',5000,'2 meses');
INSERT INTO services (name,description,cost,duration) values ('Implantes dentales','Los implantes dentales son tornillos de titanio que se colocan quirúrgicamente en los huesos maxilares, debajo de la encía, con la finalidad de recuperar dientes perdidos, o dar un soporte adicional a las prótesis removibles.',6000,'2 meses');
INSERT INTO services (name,description,cost,duration) values ('Diseño de sonrísa','Consiste en llevar los dientes a unas condiciones ideales de tamaño forma y color, según la cara, la personalidad y los rasgos específicos de cada paciente.',7000,'2 meses');
INSERT INTO services (name,description,cost,duration) values ('Ortodoncía',' Existen varios tipos de tratamientos de ortodoncia, tales como: brackets metálicos, estéticos, sin ligas e incluso sistemas totalmente transparentes.',8000,'2 meses');
INSERT INTO services (name,description,cost,duration) values ('Endodoncía','Si has notado que de repente te duele un diente cuando tomas líquidos frías o muy calientes, cuando estas acostado, cuando masticas y que el diente te punza, entonces, puedes necesitar de un tratamiento de conductos o endodoncia.',9000,'2 meses');
INSERT INTO services (name,description,cost,duration) values ('Periodoncía','La salud de las encías es tan importante como la de las piezas dentales. Un cuidado deficiente de las encías puede derivar en patologías como el sarro, gingivitis o la periodontitis, causando inflamación y provocando sangrado.',3000,'2 meses');
INSERT INTO services (name,description,cost,duration) values ('Resinas de alta estetica','Las resinas de alta estética es una solución para corregir: pequeñas imperfecciones, fracturas menores, caries, alteraciones de color, formas inadecuadas, dientes pequeños, etc..',400,'2 meses');